<?php

namespace PSL\ClipperBundle\Entity\Repository;

use Doctrine\ORM\EntityRepository;
use Doctrine\Common\Persistence\ObjectManager;
use Doctrine\Common\Collections\Criteria;

// custom
use PSL\ClipperBundle\Entity\FirstQProject as FirstQProject;

/**
 * FirstQProjectRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class FirstQProjectRepository extends EntityRepository
{
  public function findByIdsAndState($ids = array(), $state)
  {
    $expr = Criteria::expr();
    $criteria = Criteria::create();
    $criteria->where($expr->in('bc_product_id', $ids));
    $criteria->andWhere($expr->eq('state', $state));
    
    return $this->matching($criteria);
  }
  
  public function findByState($state)
  {
    $expr = Criteria::expr();
    $criteria = Criteria::create();
    $criteria->where($expr->eq('state', $state));
    
    return $this->matching($criteria);
  }
  
  public function findByStateNot($state)
  {
    $expr = Criteria::expr();
    $criteria = Criteria::create();
    $criteria->where($expr->neq('state', $state));
    
    return $this->matching($criteria);
  }
  
  public function getFormDataByField(FirstQProject $fq, $field_name) 
  {
    $response = array();
    $raw = $fq->getFormDataRaw();
    $unserialized = unserialize($raw);
    if (isset($unserialized->{$field_name})) {
      $response = (array)$unserialized->{$field_name};
    }
    
    return $response;
  }
  
}
